  private void showPassActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_showPassActionPerformed
      if (showPass.isSelected()) {
          userPass.setEchoChar((char) 0);
      } else {
          userPass.setEchoChar('*');
      }
  }//GEN-LAST:event_showPassActionPerformed

  private void btnExitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExitActionPerformed
      int confirmed = JOptionPane.showConfirmDialog(null,
              "Are you sure about that?", "Exit",
              JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE);
      if (confirmed == JOptionPane.YES_OPTION) {
          System.exit(0);
      }
  }//GEN-LAST:event_btnExitActionPerformed

  private void btnLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLoginActionPerformed
      if (isEmpty()) {
          String uname = userText.getText();
          String upass = String.valueOf(userPass.getPassword());
          if (uname.equals("cashier") && upass.equals("pos")) {

              Dashboard dashboard = new Dashboard();
              dashboard.setVisible(true);
              setVisible(false);

          } else {
              JOptionPane.showMessageDialog(null, "Invalid Username or Password!", "Invalid", JOptionPane.ERROR_MESSAGE);
          }
      }
  }//GEN-LAST:event_btnLoginActionPerformed

  private void jPanel1MouseDragged(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jPanel1MouseDragged
      int x = evt.getXOnScreen();
      int y = evt.getYOnScreen();
      this.setLocation(x - xx, y - xy);
  }//GEN-LAST:event_jPanel1MouseDragged

  private void jPanel1MousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jPanel1MousePressed
      xx = evt.getX();
      xy = evt.getY();
  }//GEN-LAST:event_jPanel1MousePressed

  private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
      for (double i = 0.0; i <= 1.0; i += 0.1) {
          String s = i + "";
          float f = Float.valueOf(s);
          this.setOpacity(f);
          try {
              Thread.sleep(40);
          } catch (InterruptedException ex) {
              Logger.getLogger(Login.class.getName()).log(Level.SEVERE, null, ex);
          }
      }
  }//GEN-LAST:event_formWindowOpened

