    private void userTextFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_userTextFocusLost
        if (userText.getText().isEmpty()) {
            userText.setText("Username");
        }
    }//GEN-LAST:event_userTextFocusLost

    private void userPassFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_userPassFocusLost
        if (userPass.getText().isEmpty()) {
            userPass.setText("Password");
        }
    }//GEN-LAST:event_userPassFocusLost

    public boolean isEmpty() {
        if (userText.getText().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Username or Password is missing.", "Bruhh", JOptionPane.ERROR_MESSAGE);
            return false;
        }
        if (String.valueOf(userPass.getPassword()).isEmpty()) {
            JOptionPane.showMessageDialog(null, "Password is missing.", "Bruhh", JOptionPane.ERROR_MESSAGE);
            return false;
        }
        return true;
    }

